dt-option.example:
$('#myTable').DataTable( {
	rowGroup: {
		endRender: function ( rows, group ) {
			return group +' ('+rows.count()+' rows)';
		}
	}
} );

$('#myTable').DataTable( {
	rowGroup: {
		startRender: null,
		endRender: function ( rows, group ) {
			return group +' ('+rows.count()+' rows)';
		}
	}
} );

$('#myTable').DataTable( {
	rowGroup: {
		endRender: function ( rows, group ) {
			var avg = rows
				.data()
				.pluck('salary')
				.reduce( function (a, b) {
					return a + b.replace(/[^\d]/g, '')*1;
				}, 0) / rows.count();

			return 'Average salary in '+group+': '+
				$.fn.dataTable.render.number(',', '.', 0, '$').display( avg );
		}
	}
} );
