dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		ajax: '../../../../examples/ajax/data/objects.txt',
		columns: [
			{ data: 'name' },
			{ data: 'position' },
			{ data: 'office' },
			{ data: 'extn' },
			{ data: 'start_date' },
			{ data: 'salary' }
		],
		order: [[2, 'asc']],
		rowGroup: {
			dataSrc: 'office'
		}
	} );
} );
