dt-example.css:
table.dataTable tr.group-end td {
	text-align: right;
	font-weight: normal;
}

dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		order: [[3, 'asc']],
		rowGroup: {
			dataSrc: function ( row ) {
				var base = Math.floor(row[3] / 10);
				return "" + base + "0 - " + base + '9';
			}
		}
	} );
} );
