dt-example.css:
table.dataTable tr.group-end td {
	text-align: right;
	font-weight: normal;
}

dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		order: [[2, 'asc']],
		rowGroup: {
			endRender: function ( rows, group ) {
				var avg = rows
					.data()
					.pluck(5)
					.reduce( function (a, b) {
						return a + b.replace(/[^\d]/g, '')*1;
					}, 0) / rows.count();

				return 'Average salary in '+group+': '+
					$.fn.dataTable.render.number(',', '.', 0, '$').display( avg );
			},
			dataSrc: 2
		}
	} );
} );
